(defn TrialHong []
  (def key-idx (index-of "홍문종" members))

  ; Encoding - 1 if same with key member, 0 elsewhere.
  (defn encode-row [vote]
    (let [key-choice (nth vote key-idx)]
      (map (fn [x] (if (= key-choice x) 1 0)) vote)))

  ; ((true true true true true false ...) ...)
  (def encoded-vote
    (map encode-row (filter
                      (fn [row] (not (= (nth row key-idx) "abs")))
                      (map rest (rest vote_result)))))

  (defn vote-ith [i]
    (map (fn [row] (nth row i)) encoded-vote))

  (def probs-hong
    (vec (map (fn [x] (sample-mean (sample-prob x 100)))
              (range 0 (count members)))))

  (defquery inference  [probs]
  (let [a (sample (uniform-continuous 0 1)) ; parameter for trans.
        b (sample (uniform-continuous 0.1 1)) ; parameter for trans.
        trans (max (min (sample (normal a b)) 1.5) 0.5)]
    (loop [sum 0
           ps probs] 
      (if (empty? ps)
        (do
          (observe (normal sum 4) 141)
          trans)
        (let [p-con (* (first ps) trans)
              is-con (if (sample (flip (min p-con 1.0))) 1 0)]
              (recur (+ sum is-con)
                   (rest ps)))))))

  (def trans-samples-hong
  	(vec
    	(map :result
    	   (take 1000 (take-nth 50 (drop 1000 (doquery :ipmcmc inference [probs-hong])))))))
  (def trans-mean-hong (/ (reduce + 0.0 trans-samples-hong) (count trans-samples-hong)))
  (def mem-except-hong (filter (fn [x] (not (= x "홍문종"))) members))
  (def fin-probs-hong (map (fn [x] (* x trans-mean-hong)) probs-hong))
  (def hong-points (filter (fn [x] (not (= (first x) "염동열"))) (map vector mem-except-hong fin-probs-hong)))
  (take 30 (reverse (sort-by second
       (filter (fn [x] (= (second (nth party-info (index-of (first x) members))) "더불어민주당"))
 	hong-points)))))
