;; **
;;; #### Observe에 0,1-리스트를 넘기는 경우
;; **

;; @@
(defquery single [obs]
  (let [a (+ 1(sample (poisson 1)))
        b (+ 1(sample (poisson 1)))]
   (map (fn [x] (observe (beta a b) x)) obs)
 (sample (beta a b))))
