;; **
;;; ### Sample Mean Distance
;; **

;; @@
(defn get-sample-mean-abs [key-idx idx]
  (let [enc  (map encode-row-2 (filter
                    (fn [row] (not (= (nth row key-idx) "abs")))
                    (map rest (rest vote_result))))
        lazy (doquery :ipmcmc single [(map (fn [x] (nth x idx)) enc)])
        samp (map :result (take-nth 10 (take 500 (drop 1000 lazy))))
        mean (sample-mean samp)]
    mean))

(defn get-sample-mean [key-idx idx]
  (let [ob   (map encode-row (filter
                    (fn [row] (not (or (= (nth row key-idx) "abs") (= (nth row idx) "abs"))))
                    (map rest (rest vote_result))))
        enc  (map (fn [y] (map (fn [x] (if x 0.9 0.01)) y)) ob)
        lazy (doquery :ipmcmc single [(map (fn [x] (nth x idx)) enc)])
        samp (map :result (take-nth 10 (take 500 (drop 1000 lazy))))
        mean (sample-mean samp)]
   mean))

(defn mean-farthest-k-with-abs [target k]
  (let [name-dist-pairs (map vector members (map (fn [x] (get-sample-mean-abs target (index-of x members))) members))
        sorted-pairs    (sort-by second name-dist-pairs)
        top-k           (take k sorted-pairs)]
   top-k))
(defn mean-farthest-k [target k]
  (let [name-dist-pairs (map vector members (map (fn [x] (get-sample-mean target (index-of x members))) members))
        sorted-pairs    (sort-by second name-dist-pairs)
        top-k           (take k sorted-pairs)]
   top-k))
