(defn encode-row-2 [vr-row]
  (let [key-choice (nth vr-row key-idx)]
    (map (fn [x] (if (= key-choice x) 0.9
                   (if (not (= x "abs")) 0.01
                   	(if (or (= key-choice "pro") (= key-choice "con")) 0.5 0.01)))) vr-row)))

(defn test-2 [idx]
  (let [enc  (map encode-row-2 (filter
                    (fn [row] (not (= (nth row key-idx) "abs")))
                    (map rest (rest vote_result))))
        lazy (doquery :ipmcmc single [(map (fn [x] (nth x idx)) enc)])
        samp (map :result (take-nth 10 (take 10000 (drop 5000 lazy))))
        mean (sample-mean samp)]
   (print (nth members key-idx) " " (nth members idx) ": " (count enc) "\n")
   (print "Sample Mean: " mean "\n")
   (plot/histogram samp :normalize :probability :bins 60)))
